    fun getTemperature(ci: Double) : Int{
        val bv = (ci - 0.01) / 0.57 // Landolt's result. See http://brucegary.net/photometry/V-R.htm
        val temp = 5000.0 * ((1.0 / (0.92 * bv + 1.7)) + (1.0 / (0.92 * bv + 0.62)))
        return 100 * (temp / 160.0).toInt()
    }
